import { IExceptionDetails } from "@microsoft/applicationinsights-common/types/Interfaces/Contracts/IExceptionDetails";
import { IDomain } from "@microsoft/applicationinsights-common/types/Interfaces/Contracts/IDomain";
import { SeverityLevel } from "@microsoft/applicationinsights-common/types/Interfaces/Contracts/SeverityLevel";
/**
 * An instance of Exception represents a handled or unhandled exception that occurred during execution of the monitored application.
 */
export interface IExceptionData extends IDomain {
    /**
     * Schema version
     */
    ver: number;
    /**
     * Exception chain - list of inner exceptions.
     */
    exceptions: IExceptionDetails[];
    /**
     * Severity level. Mostly used to indicate exception severity level when it is reported by logging library.
     */
    severityLevel: SeverityLevel;
    /**
     * Collection of custom properties.
     */
    properties: any;
    /**
     * Collection of custom measurements.
     */
    measurements: any;
}
